export class MapAction{constructor(t){Array.isArray(t)||(t=[t]),this.interactions=[],t.forEach(t=>{this.addInteraction(t)})}addInteraction(t){t.setActive(!1),this.interactions.push(t)}activate(){this.interactions.forEach(t=>{t.setActive(!0)})}deactivate(){this.interactions.forEach(t=>{t.setActive(!1)})}}
